content:
{
  "stage": "Plan",
  "iteration": "1",
  "tasks": [
    {
      "id": "t1",
      "name": "Draft answer for QA intent",
      "objective": "Produce a concise, accurate answer addressing the user's QA intent based on the provided analysis context.",
      "inputs": {
        "user_intent": "qa",
        "analysis": {
          "stage": "Analyze"
        },
        "constraints": "Respond only with strict JSON in the Plan step; subsequent answer should be concise and accurate"
      },
      "outputs": {
        "draft_answer": "string (concise response addressing the QA intent)",
        "metadata": {
          "keywords": "array of strings",
          "confidence_estimate": "number 0-1"
        }
      },
      "actions": [
        "Interpret the QA intent and analysis context to determine the user's likely question scope",
        "Compose a concise, direct answer addressing that scope; avoid extraneous explanation",
        "Include brief metadata: keywords and a confidence estimate"
      ],
      "dependencies": [],
      "acceptance": [
        "Draft directly addresses the QA intent (concise factual answer)",
        "No contradictory statements",
        "Metadata present: at least 1 keyword and a numeric confidence"
      ],
      "risks": [
        "Misinterpreting the user's intended scope for 'qa'",
        "Omitting crucial details leading to an incomplete answer"
      ],
      "mitigations": [
        "Prefer the minimal, common-sense interpretation of 'qa' (answering a question clearly)",
        "If ambiguous, produce a short clarifying question as part of the draft"
      ]
    },
    {
      "id": "t2",
      "name": "Self-validate draft for correctness and clarity",
      "objective": "Verify the draft answer for factual correctness, clarity, and adherence to constraints; produce the final verified answer.",
      "inputs": {
        "draft_answer": "string (output of t1)",
        "verification_checks": [
          "factual_consistency",
          "conciseness",
          "readability",
          "no forbidden formatting"
        ]
      },
      "outputs": {
        "verified_answer": "string (final answer ready to deliver)",
        "validation_report": {
          "checks_passed": "array of strings",
          "checks_failed": "array of strings",
          "adjustments_made": "string (if any)"
        }
      },
      "actions": [
        "Run factual_consistency check against internal knowledge and the analysis context",
        "Assess conciseness and readability; trim or rephrase if verbose",
        "Ensure formatting constraints are met (no markdown fences, plain text unless otherwise requested)",
        "Produce a validation report summarizing checks and any edits"
      ],
      "dependencies": [
        "t1"
      ],
      "acceptance": [
        "All verification checks passed or documented adjustments made",
        "Final answer is concise, accurate, and formatted per constraints",
        "Validation report included"
      ],
      "risks": [
        "Verification may miss subtle factual errors",
        "Edits could unintentionally alter intended meaning"
      ],
      "mitigations": [
        "Conservatively prefer short clarifying questions when in doubt rather than introducing potentially incorrect facts",
        "Document any edits in the validation_report for traceability"
      ]
    }
  ],
  "notes": "Two-step micro-iteration: (1) draft a concise QA response from the analysis, (2) validate and finalize the answer. Tasks are deterministic and machine-actionable; t2 depends on t1."
}
